			[21] = { str = "Go back into Brill and turn in all quests which include #INA New Plague#, #INWanted: Maggot Eye# and #INGraverobbers# then get all new quests which include #GETA New Plague#, #GETForsaken Duties# and #GETThe Prodical Lich#." },
			[22] = { str = "Travel to the tower at #COORD[53,66]# and kill 3 Scarlet Missionaries, 3 Scarlet Zealots and Captain Parrine #COORD[51,68]# make sure you loot all 10 Scarlet Insignia Rings.", x = 53, y = 66, zone = "Tirisfal Glades" },
